function addAnchors(t){t=t||"h1, h2, h3, h4, h5, h6";for(var e=document.querySelectorAll(t),n=0;n<e.length;n++){var r;if(e[n].hasAttribute("id"))r=e[n].getAttribute("id");else{var i=document.body.textContent?"textContent":"innerText",a=e[n][i];tidyText=a.replace(/\s+/g,"-").toLowerCase(),e[n].setAttribute("id",tidyText),r=tidyText}var o='<a class="anchor-link" href="#'+r+'"><span class="icon-link"></span></a>';e[n].innerHTML=e[n].innerHTML+o}}